# No need to import anything for this homework!def english_to_piglatin(word):    word = word.upper()    if word[0] in "AEIOU":        newword = word[0]+'WAY'       elif word[1] in "AEIOU":        newword = word[1:]+word[0]+'AY'    elif word[2] in "AEIOUY":        newword = word[2:]+word[0]+word[1]+'AY'    elif len(word) == 4:        if word[3] in "AEIOUY":            newword = word[3:]+word[0]+word[1]+ word[2]+'AY'    else:        return(word)    return(newword)#Function only takes in one word at the timeenglish_to_piglatin("SKRZYPCZAK")def admitted(sat, act, rank, gpa):    #Rank checker first.    if rank == 0:        if sat >= 1250 and gpa >= 3.5 or act >= 28 and gpa >= 3.5:            return(True)        else:            return(False)    elif sat == 0 and act == 0:        return TypeError("Both exam scores are 0")    elif sat >= 1250 and rank <= 0.2 or act >= 28 and rank <= 0.2:            return(True)    else:            return(False)admitted(1300, 0, 0, 3.2)admitted(0,30,0.15,3.1)def improvement_needed(sat, act, rank, gpa):    if admitted(sat, act, rank, gpa) == False:        ###SAT####        if sat >= 1250:            newsat = 0        elif act >= 28:            newsat = 0        else:            newsat = 1250 - sat        ###ACT####        if act >= 28:            newact = 0        elif sat >= 1250:            newact = 0        else:            newact = 28 - act        ###RANK####        if rank <= 0.2:            newrank = 0        #Round to avoid any weird decimal outputs        else:            newrank = round(rank - 0.2, 4)        ###GPA####        if gpa >= 3.5:            newgpa = 0        elif rank == 0:            newgpa = 3.5 - gpa        elif gpa >= 3:            newgpa = 0        else:            newgpa = round(3 - gpa, 4)        return(newsat, newact, newrank, newgpa)    else:        return(0,0,0,0)    improvement_needed(1200, 0, 0.3, 2.8)improvement_needed(1300, 25, 0.5, 2.5)improvement_needed(1000, 20, 0.1, 3.2)improvement_needed(1500, 20, 0.1, 3.8)def isvalid(sat, act, rank, gpa):    if not (sat <= 1600 and sat >= 0):        return(False)    elif not (act <= 36 and act >= 0):        return(False)    elif not (rank <= 1 and rank >= 0):        return(False)    elif not (gpa <= 4 and gpa >= 0):        return(False)    else:        return(True)  