#Problem4a = int(input())def get_input(x):    data = [0] * x    for i in range(x):        data[i] = int(input())    return datadata = get_input(a)print(data)def solution(data):    for i in range(len(data)):        if data[i] >= 0:            data[i] = 1        else:            data[i] = 0    x = 0    for i in range(len(data)):        x = x + data[i]    return(bool(x>=3))answer = solution(data)print(answer)